                {(this.state.ethBrowser && this.state.isLogged && this.state.announce && !this.state.loading && this.state.designer) && (<form onSubmit={this.captureFile2}>
               <label>
                Design description:
                <textarea onChange={this.captureFile5} />
               </label>
               </form>)}
               { (this.state.ethBrowser && this.state.isLogged && this.state.announce && !this.state.loading && this.state.designer) &&(<form>
                             commitment:
                               <CurrencyInput value={this.state.amount} onChangeEvent={this.setCommit}
                               CurrencyInput suffix=" ETH" />
                          </form>) }
               { (this.state.ethBrowser && this.state.isLogged && this.state.announce && !this.state.loading && this.state.designer) &&(<form>
                              reward amount:
                                  <CurrencyInput value={this.state.reward} onChangeEvent={this.setReward}
                                  CurrencyInput suffix=" ETH" />
                          </form>) }
                { (this.state.ethBrowser && this.state.isLogged && this.state.announce && !this.state.loading && this.state.designer) &&(<form>
                               penality amount:
                                    <CurrencyInput value={this.state.penality} onChangeEvent={this.setPenality}
                                    CurrencyInput suffix=" ETH" />
                          </form>) }
                { (this.state.ethBrowser && this.state.isLogged && this.state.announce && !this.state.loading && this.state.designer) &&(<form>
                                expiry time:
                                        <NumericInput  min={0} max={120} step={1} format={expiryFormat} mobile="auto" onChange={this.setExpiry}/>
                                </form>) }
                { (this.state.ethBrowser && this.state.isLogged && this.state.announce && !this.state.loading && this.state.designer) &&(<form>
                                reveal time:
                                       <NumericInput min={0} max={120} step={1} format={expiryFormat} mobile="auto" onChange={this.setReveal}/>
                                    </form>) }
              {(this.state.ethBrowser && this.state.isLogged && this.state.announce && !this.state.loading && this.state.designer) && (<button className="button" onClick={this.onSubmit}>Submit</button>)}
              {(this.state.ethBrowser && this.state.isLogged && this.state.announce && !this.state.loading && this.state.designer) && (<form >
              <label>
             Select design format:
             <select onChange = {this.formatSelect}>
             <option value="stl">.stl</option>
             <option value="obj">.obj</option>
          </select>
        </label>
      </form>)}
      { (this.state.ethBrowser && this.state.isLogged && this.state.announce && !this.state.loading && this.state.designer) && (
        <button className="button" onClick={this.logoutU}>Logout</button>)
      }
      { (this.state.ethBrowser && this.state.isLogged && this.state.announce && !this.state.loading && this.state.designer) && (
        <button className="button" onClick={this.toMainU}>Return</button>)
      }
      {/*end announce section*/}









     {/*calculate result*/}

     { (this.state.ethBrowser && this.state.isLogged && !this.state.loading && this.state.designer && this.state.calculateResult && !this.state.manager) &&
         (<ul>
           <table ><tr><th >Design Number</th><th>design</th></tr>{this.state.avaibleDesign.map((item)=><tr className="table"  onClick={this.provaC}><td>{item.designNo.toString()}</td><td>{item.filehash.toString()}</td><td><button  className="button" value={item.designNo.toString()} onClick={this.calculateResult}>Select</button></td></tr>)}</table>
       </ul>)}
       { (this.state.ethBrowser && this.state.isLogged && !this.state.loading && this.state.designer && this.state.calculateResult && !this.state.manager) && (
         <p>result:{this.state.result}</p>)
       }
       { (this.state.ethBrowser && this.state.isLogged && !this.state.loading && this.state.designer && this.state.calculateResult && !this.state.manager) && (
         <button  className="button" onClick={this.toMainCR}>Return</button>)
       }

     {/*end calculate result*/}










      {/*manager part*/}

      { (this.state.ethBrowser && this.state.isLogged && this.state.main && !this.state.loading && this.state.manager) && (
      <p>manager</p>)
      }
      { (this.state.ethBrowser && this.state.isLogged && this.state.main && !this.state.loading && this.state.player && this.state.manager) && (
      <button className="button" id="param29" onClick={this.toCurrentDesignsM} >validate registered players</button>)
      }




      { (this.state.ethBrowser && this.state.isLogged && !this.state.loading && this.state.player && this.state.currentDesignsM && this.state.manager && !this.state.designSelectedM) &&
          (<ul>
            <table ><tr><th >Design Number</th><th>creatorAddress</th><th>design</th><th>avaibility</th></tr>{this.state.avaibleDesign.map((item)=><tr className="table"  onClick={this.provaC}><td>{item.designNo.toString()}</td><td>{item.vendor.toString()}</td><td>{item.filehash.toString()}</td><td>{item.expired.toString()}</td><td><button  className="button" value={item.designNo.toString()} onClick={this.designSelected1M}>Select</button></td></tr>)}</table>
        </ul>)}
    { (this.state.ethBrowser && this.state.isLogged && !this.state.loading && this.state.player  && this.state.manager && this.state.designSelectedM) &&
            (<ul>
              <table ><tr><th>Player Address</th></tr>{this.state.playerList.map((item)=><tr className="table"  onClick={this.provaC}><td>{item.toString()}</td><td><button  className="button" value={item.toString()} onClick={this.designSelected2M}>Select</button></td></tr>)}</table>
          </ul>)}
        { (this.state.ethBrowser && this.state.isLogged && !this.state.loading && this.state.player && (this.state.currentDesignsM || this.state.designSelectedM) && this.state.manager) && (
          <button  className="button" onClick={this.toMainCM}>Return</button>)
        }
        { (this.state.ethBrowser && this.state.isLogged && !this.state.loading && this.state.player && (this.state.currentDesignsM || this.state.designSelectedM) && this.state.manager) && (
          <button  className="button" onClick={this.refreshCM}>Refresh</button>)
        }
      {/*end manager part*/}






              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
