import React, { Component } from 'react';
import Web3 from 'web3';
import './App.css';
import ReactDOM from 'react-dom';
import Meme from '../abis/Meme.json'
//connessione al nodo di infura per ipfs
const ipfsClient = require('ipfs-http-client')
const ipfs = ipfsClient({ host: 'ipfs.infura.io', port: 5001, protocol: 'https' })

class App extends Component {
//operazioni quando il contratto viene chiamato
  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }
//carico la finestra web3 o la converto in web3
  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. ')
    }
  }
// dati della blockchain:
//account è l'indirizzo di colui che firma la transazione
//networkid è l'id della connessione es:localhost:7545
//contract è la variabile del contratto
//designHash è l'hash del file salvato sull'ipfs
  async loadBlockchainData() {
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()
    const networkData = Meme.networks[networkId]
    if(networkData) {
      const contract = web3.eth.Contract(Meme.abi, networkData.address)
      this.setState({ contract })
      const designHash = await contract.methods.get().call()
      this.setState({ designHash })
    } else {
      window.alert('Smart contract not deployed to detected network.')
    }
  }

  constructor(props) {
    super(props)

    this.state = {
      designHash: '',
      contract: null,
      web3: null,
      buffer: null,
      account: null,
      name: '',
      surname:''
    }
  }

  captureFile = (event) => {
    event.preventDefault()
    const file = event.target.files[0]
    const reader = new window.FileReader()
    reader.readAsArrayBuffer(file)
    reader.onloadend = () => {
      this.setState({ buffer: Buffer(reader.result) })
      console.log('buffer', this.state.buffer)
    }
  }
 captureFile2 = (event) => {
 event.preventDefault()
 console.log("charging ")
 const stri = event.target.value
 const reader = new window.FileReader()
 var buf = Buffer.from(stri, 'utf-8')
 this.setState({ name: buf })
 console.log('buffer', this.state.name)
 }
 captureFile3 = (event) => {
 event.preventDefault()
 console.log("charging 2")
 const stri = event.target.value
 const reader = new window.FileReader()
 var buf = Buffer.from(stri, 'utf-8')
 this.setState({ surname: buf })
 console.log('buffer', this.state.surname)
 }
  onSubmit = (event) => {
    event.preventDefault()
    console.log("Submitting design to ipfs")
    ipfs.add(this.state.buffer, (error, result) => {
      console.log('Ipfs result', result)
      if(error) {
        console.error(error)
        return
      }
       this.state.contract.methods.set(result[0].hash).send({ from: this.state.account }).then((r) => {
         return this.setState({ designHash: result[0].hash })
       })
    })
  }
onSubmit2 = (event) => {
  event.preventDefault()
  console.log("submitting metafile to ipfs")
  ipfs.add(this.state.name, (error, result) => {
    console.log('Ipfs result', result)
    if(error) {
      console.error(error)
      return
    }
     console.log(result[0].hash)
  })
}
onSubmit3 = (event) => {
  event.preventDefault()
  console.log("submitting metafile to ipfs2")
  ipfs.add(this.state.surname, (error, result) => {
    console.log('Ipfs result', result)
    if(error) {
      console.error(error)
      return
    }
     console.log(result[0].hash)
  })
}
  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            target="_blank"
            rel="noopener noreferrer"
          >
            App di prova
          </a>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  <img src={`https://ipfs.infura.io/ipfs/${this.state.designHash}`} />
                </a>
                <p>&nbsp;</p>
                <h2>Change design</h2>
                <form onSubmit={this.onSubmit} >
                  <input type='file' onChange={this.captureFile} />
                  <input type='submit'  value = "Send"/>
                <p>&nbsp;</p>
                </form>
                <form onSubmit={this.onSubmit2} >
                <p>&nbsp;</p>
                <label>
                Name:
                <input type="text" /*value={this.state.name} */onChange={this.captureFile2} />
                </label>
                <input type="submit" value="Submit" />
                </form>
              </div>
              <div>
              <form onSubmit={this.onSubmit3} >
              <p>&nbsp;</p>
              <label>
              Surname:
              <input type="text" value={this.state.surname} onChange={this.captureFile3} />
              </label>
              <input type="submit" value="Submit" />
              </form>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
