import React, { Component } from 'react';
import Web3 from 'web3';
import './App.css';
import ReactDOM from 'react-dom';
import Dapp from '../abis/Dapp.json'
import {OBJViewer, STLViewer} from 'react-stl-obj-viewer';
//connessione al nodo di infura per ipfs
const ipfsClient = require('ipfs-http-client')
const ipfs = ipfsClient({ host: 'ipfs.infura.io', port: 5001, protocol: 'https' })
//tipi di formato .stl (più comune) 3MF AMF e .obj(open source)
// usa accept
// url example:'https://ipfs.infura.io/ipfs/QmZsdEtccredWWGZzZvU5fStsC7aWADJch588qAarGTky9'
//{`https://ipfs.infura.io/ipfs/${this.state.designHash}`}
class App extends Component {

//operazioni quando il contratto viene chiamato
  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }
//carico la finestra web3 o la converto in web3
  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. ')
    }
  }
// dati della blockchain:
//account è l'indirizzo di colui che firma la transazione
//networkid è l'id della connessione es:localhost:7545
//contract è la variabile del contratto
//designHash è l'hash del file salvato sull'ipfs
  async loadBlockchainData() {
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })
    const networkId = await web3.eth.net.getId()
    const networkData = Dapp.networks[networkId]
    if(networkData) {
      const contract = web3.eth.Contract(Dapp.abi, networkData.address)
      this.setState({ contract })
      const designHash = await contract.methods.get().call()
      this.setState({ designHash })
    } else {
      window.alert('Smart contract not deployed to detected network.')
    }
  }

  constructor(props) {
    super(props)

    this.state = {
      designHash: '',
      contract: null,
      web3: null,
      buffer: null,
      account: null,
      designName: '',
      makerName:'',
      makerSurname:'',
      desc:'',
      stlLink:''
    }
  }
  submitMetaFile = (event) => {
    event.preventDefault()
    console.log("submitting metadata to IPFS")
    const designName = this.state.designName;
    const desc = this.state.desc;
    const makerName = this.state.makerName;
    const makerSurname = this.state.makerSurname;
    const designHash = this.state.designHash;
    if (designName == '' || makerName == '' || makerSurname == '' || desc == ''){
      console.log("metadata not saved")
      this.setState({designName:'',makerName:'',makerSurname:'',desc:''})
      return
    }
   else{
     if(designHash == ''){
       console.log("design not uploaded")
     }
     else{
         const toJSON = {
             designName : designName,
             makerName : makerName,
             makerSurname : makerSurname,
             desc : desc,
             designHash : designHash
             };
     const myJson = JSON.stringify(toJSON) ;
     console.log(toJSON)
     console.log(myJson)
     var buf = Buffer.from(myJson);
     ipfs.add(buf, (error, result) => {
       console.log('Ipfs result', result)
       if(error) {
         console.error(error)
         return
       }
        console.log(result[0].hash)
     })
     this.setState({designName:'',makerName:'',makerSurname:'',desc:''})
   }
   }
  }

  captureFile = (event) => {
    event.preventDefault()
    const file = event.target.files[0]
    const reader = new window.FileReader()
    reader.readAsArrayBuffer(file)
    reader.onloadend = () => {
      this.setState({ buffer: Buffer(reader.result) })
      console.log('buffer', this.state.buffer)
    }
  }
 captureFile2 = async (event) => {
 event.preventDefault()
 console.log("charging designName")
 const stri = event.target.value
 await this.setState({ designName: stri })
 console.log(this.state.designName)
 }
 captureFile3 = async (event) => {
 event.preventDefault()
 console.log("charging maker name")
 const stri = event.target.value
 await this.setState({ makerName: stri})
 }
 captureFile4 = async (event) => {
 event.preventDefault()
 console.log("charging maker surname")
 const stri = event.target.value
 await this.setState({ makerSurname: stri})
 }
 captureFile5 = async (event) => {
 event.preventDefault()
 console.log("charging design description")
 const stri = event.target.value
 await this.setState({ desc: stri})
 console.log(this.state.desc)
 }
  onSubmit = (event) => {
    event.preventDefault()
    console.log("Submitting design to ipfs")
    ipfs.add(this.state.buffer, (error, result) => {
      console.log('Ipfs result', result)
      if(error) {
        console.error(error)
        return
      }
       this.state.contract.methods.set(result[0].hash).send({ from: this.state.account }).then((r) => {
         return this.setState({ designHash: result[0].hash })
       })
    })
    console.log(`https://ipfs.infura.io/ipfs/${this.state.designHash}`)
    const link = `https://ipfs.infura.io/ipfs/${this.state.designHash}`
    this.setState({ stlLink: link })
  }
  render() {
    return (
      <div>
        <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
          <a
            className="navbar-brand col-sm-3 col-md-2 mr-0"
            target="_blank"
            rel="noopener noreferrer"
          >
            App di prova
          </a>
        </nav>
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  target="_blank"
                  rel="noopener noreferrer"
                >
                </a>
                Design caricato:
                <STLViewer
                            id="myImage"
                            onSceneRendered={(element) => {
                                console.log(element)
                            }}
                            sceneClassName="test-scene"
                            url={this.state.stlLink}
                            className="obj"
                            modelColor="#FF0000"/>

                <p>&nbsp;</p>
                <h2>Change design</h2>
                <form onSubmit={this.onSubmit} >
                  <input type='file' accept=".stl,.obj,.3mf,.amf" onChange={this.captureFile} />
                  <input type='submit'  value = "Send"/>
                <p>&nbsp;</p>
                </form>
                <form >
                <p>&nbsp;</p>
                <label>
                Design title:
